mapper.insert:
INSERT INTO
    club_calendar(club_calendar_num, club_num,reg_date,calendar_title,calendar_text,club_date,location,notice_check,notice_time,calendar_apply_check,apply_auto_check,board_category)
    values(  SEQ_CLUB_CALENDAR.nextval
        ,#{clubNum}
        ,SYSDATE
        ,#{calenderTitle}
        ,#{calenderText}
        ,#{clubDate}
        ,#{location}
        ,#{noticeCheck}
        ,#{noticeTime}
        ,#{calendarApplyCheck}
        ,#{applyAutoCheck}
        ,#{boardCategory})

   SELECT SEQ_CLUB_CALENDAR.CURRVAL from  DUAL

mapper.update:
UPDATE
    club_calendar

        calendar_title = #{calenderTitle}
        ,calendar_text = #{calenderText}
        ,location = #{location}
        ,notice_check = #{noticeCheck}
        ,club_date = #{clubDate}
        ,notice_time = #{noticeTime}
        ,calendar_apply_check = #{calendarApplyCheck}
        ,apply_auto_check = #{applyAutoCheck}
        ,reg_date = SYSDATE

    WHERE club_calendar_num = #{clubCalenderNum}

