mapper.update:
UPDATE users

    user_name = #{userName:VARCHAR},
    rrd = #{rrd:VARCHAR},
    vill_code = #{villCode:VARCHAR}

WHERE user_id=#{userId:VARCHAR}

mapper.select:
SELECT user_id
FROM users
WHERE user_name=#{userName} and email=#{email} and LOGIN_TYPE=#{loginType}

SELECT user_id
FROM users
WHERE  user_name=#{userName} and phone=#{phone} and LOGIN_TYPE=#{loginType}

SELECT password
FROM users
WHERE user_id=#{userId} and email=#{email}

SELECT password
FROM users
WHERE user_id=#{userId} and phone=#{phone}

SELECT rrd
FROM users
WHERE rrd=#{rrd}

mapper.update:
