password = #{password:VARCHAR}, email = #{email:VARCHAR}, phone = #{phone:VARCHAR}, vill_code = #{villCode:VARCHAR}, user_image = #{userImage:VARCHAR}
WHERE user_id=#{userId:VARCHAR}

