mapper.select:
SELECT user_id, user_status, user_name, gender, email, phone, vill_code, user_image,  psd, ped, ppt, review_pt, login_type
FROM users
WHERE user_id=#{userId}

SELECT inter_list
FROM user_inter_list
WHERE user_id=#{userId}
